content:
* {
  scroll-behavior: smooth;
}

html {
  width: 100vw;
  height: 100vh;
}

body {
  background: rgba(150, 220, 230, 0.9);
}

.content {
  min-height: 80vh;
  background: #FFF;
  margin-top: 0 !important;
  border-radius: 4px;
}

@media screen and (min-width: 768px) {
  .content {
    width: 900px !important;
    padding: 50px !important;
  }
  .toc-article {
    margin-left: 500px;
  }
}

.article-content h1 {
  font-size: 22px;
  margin-top: 52px;
}
.article-content h2 {
  font-size: 18px;
  margin-top:18px;
}

.article-content blockquote {
  background: rgba(0, 0, 0, 0.02);
  border-left: 6px solid #6190e8;
  padding: 1px 10px;
  border-radius: 5px;
  margin: 0 14px 14px;
  opacity: 0.9;
}

.article-content blockquote  p {
  line-height: 24px;
  border-radius: 5px;
  font-size: 14px;
}
.article-content li {
  margin-bottom: 10px;
}


.layout {
  overflow: hidden;
  position: relative;
  display: flex;
  flex-direction: row;
  width: 100vw;
  height: 100vh;
  background: url(https://img.zcool.cn/community/01c9905b858a3ca8012126ce7c37c0.jpg@1280w_1l_0o_100sh.jpg);
  background-repeat: no-repeat;
  background-size: 100% 100%;
}
.layout .category-box{
  margin-top: 100px;
}


.layout .content-category-wrap .item-post .post-title {
  font-size: 12px;
  text-overflow: ellipsis;
}

.layout .content-category {
  font-size: 12px;
  width: 100%;
}

.layout .content-category-wrap {
  position: relative;
  padding-left: 20px;
  width: 600px;
  height: 100vh;
  overflow: hidden scroll;
}

.content-category-wrap::-webkit-scrollbar {
  display: none;
}

.layout .content-home {
  position: relative;
}

.content-home .slogan,
.content-home .name {
  margin: 12px 0;
  color: #333333;
}

.article-content a {
  color: rgba(0,132,255, 0.9);
}

.page-header {
  margin-bottom: 0px !important;
}

.page-header .list-toolbox {
  display: block !important;
}

a:hover {
	color: rgba(0,132,255, 0.9) !important;
}

.pagination .next,
.pagination .prev {
  display: none !important;
}

code, kbd, pre, samp {
  font-family: monospace, monospace;
  font-size: 1em;
}

