.box-blog-info {
  display: flex !important;
  align-items: center;
  margin-top: 20px;
}

.toolbox-list {
  display: flex;
  align-items: center;
  margin: 0px 15px;
}

.item-toolbox a {
  display: inline-block;
  font-size: 12px;
  line-height: 40px;
  width: 40px;
  display: inline-block;
  height: 40px;
  margin-right: 12px;
  transition-duration: .5s;
  text-align: center;
  color: #555;
  border-radius: 20px;
  background: #f0f0f0;
  transition: box-shadow .3s ease-in-out
}

.item-toolbox a:hover {
  box-shadow: 0 0 0 3px #e6f7ff;
  background: #e6f7ff !important;
  color: #1890ff !important;
}

.gt-btn {
  background: #0084ff !important;
  opacity: 0.9;
}
.gt-btn:hover {
  opacity: 1;
  transition-duration: .5s;
}

.gt-btn-text {
  color: #FFF;
}


.content.content-search .wrap-search-box {
  margin-top: 20px;
}

.content.content-link .link-list .link-item .link-url {
  display: flex;
}

.content.content-link .link-list .link-item .wrap-info {
  margin-left: 12px;
  flex-direction: column;
  display: flex !important;
  justify-content: space-between !important;
}

.content.content-link .link-list .link-item {
  padding: 12px 0;
}

.link-list .link-item .info {
  font-size: 12px !important;
}


.box-prev-next {
  margin-top: 20px;
}

.layout .category-box {
  width: 70%;
  margin: 0 auto;
  display: flex;
  justify-content: center;
  align-items: center;
}
.category-box .category-list {
  margin-left: 0;
}

.layout .category-box .category-list .category-item .category-size {
  font-family: serif;
  font-weight: bold;
}

.category-box--top {
  margin-bottom: 15px;
}

@media screen and (max-width: 450px) {
  .layout {
    overflow: hidden;
    position: relative;
    display: flex;
    flex-direction: column;
    width: 100vw;
    height: 100vh;
    background: url(https://img.zcool.cn/community/01c9905b858a3ca8012126ce7c37c0.jpg@1280w_1l_0o_100sh.jpg);
    background-repeat: no-repeat;
    background-size: 100% 100%;
  }

  .layout .category-box,
  .layout .content-category-wrap {
    display: none;
  }
}

// Custom styles.
.hider_title{
  font-family: "Microsoft Yahei";
  cursor: pointer;
}

summary {
  font-weight: bold;
  cursor: pointer;
}

.summary::after {
  content: "▼";
}

.open:after {
  content: "▲";
